/*
 * This is the primary high-level API for the project. In this folder there should be:
 * A class called InsightFacade, this should be in a file called InsightFacade.ts.
 * You should not change this interface at all or the test suite will not work.
 */

export enum InsightDatasetKind {
	Sections = "sections",
	Rooms = "rooms",
}

export interface InsightDataset {
	id: string;
	kind: InsightDatasetKind;
	numRows: number;
}

export type InsightResult = Record<string, string | number>;

export class InsightError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, InsightError);
	}
}

export class NotFoundError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, NotFoundError);
	}
}

export class ResultTooLargeError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, ResultTooLargeError);
	}
}

export interface IInsightFacade {
	/**
	 * Add a dataset to insightUBC.
	 *
	 * @param id  The id of the dataset being added. Follows the format /^[^_]+$/
	 * @param content  The base64 content of the dataset. This content should be in the form of a serialized zip file.
	 * @param kind  The kind of the dataset
	 *
	 * @return Promise <string[]>
	 *
	 * The promise should fulfill on a successful add, reject for any failures.
	 * The promise should fulfill with a string array,
	 * containing the ids of all currently added datasets upon a successful add.
	 * The promise should reject with an InsightError describing the error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 * If id is the same as the id of an already added dataset, the dataset should be rejected and not saved.
	 *
	 * After receiving the dataset, it should be processed into a data structure of
	 * your design. The processed data structure should be persisted to disk; your
	 * system should be able to load this persisted value into memory for answering
	 * queries.
	 *
	 * Ultimately, a dataset must be added or loaded from disk before queries can
	 * be successfully answered.
	 */
	addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]>;

	/**
	 * Remove a dataset from insightUBC.
	 *
	 * @param id  The id of the dataset to remove. Follows the format /^[^_]+$/
	 *
	 * @return Promise <string>
	 *
	 * The promise should fulfill upon a successful removal, reject on any error.
	 * Attempting to remove a dataset that hasn't been added yet counts as an error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 *
	 * The promise should fulfill the id of the dataset that was removed.
	 * The promise should reject with a NotFoundError (if a valid id was not yet added)
	 * or an InsightError (invalid id or any other source of failure) describing the error.
	 *
	 * This will delete both disk and memory caches for the dataset for the id meaning
	 * that subsequent queries for that id should fail unless a new addDataset happens first.
	 */
	removeDataset(id: string): Promise<string>;

	/**
	 * Perform a query on insightUBC.
	 *
	 * @param query  The query to be performed.
	 *
	 * If a query is incorrectly formatted, references a dataset not added (in memory or on disk),
	 * or references multiple datasets, it should be rejected.
	 *
	 * @return Promise <InsightResult[]>
	 *
	 * The promise should fulfill with an array of results.
	 * The promise should reject with a ResultTooLargeError (if the query returns too many results)
	 * or an InsightError (for any other source of failure) describing the error.
	 */
	performQuery(query: unknown): Promise<InsightResult[]>;

	/**
	 * List all currently added datasets, their types, and number of rows.
	 *
	 * @return Promise <InsightDataset[]>
	 * The promise should fulfill an array of currently added InsightDatasets, and will only fulfill.
	 */
	listDatasets(): Promise<InsightDataset[]>;
}

import fs from "fs-extra";
import {
	IInsightFacade,
	InsightDataset,
	InsightDatasetKind,
	InsightError,
	InsightResult,
	NotFoundError,
	ResultTooLargeError,
} from "./IInsightFacade";
import Section from "./Section";
import { validateQuery, validateCols, isEmpty, OptionResult, MFIELDS, SFIELDS } from "./ValidationHelpers";
import DatasetProcessor from "./DatasetProcessor";
import Room from "./Room";

/**
 * This is the main programmatic entry point for the project.
 * Method documentation is in IInsightFacade
 *
 */
export default class InsightFacade implements IInsightFacade {
	private datasets: Map<string, Section[] | Room[]> = new Map<string, Section[] | Room[]>();
	private static readonly MFIELDS = MFIELDS;
	private static readonly SFIELDS = SFIELDS;
	private static readonly MAX_RESULTS = 5000;

	public async getDataset(id: string): Promise<Section[] | Room[]> {
		if (this.datasets.has(id)) {
			const dataObject = this.datasets.get(id);
			if (typeof dataObject !== "undefined") {
				return dataObject;
			}
		}
		try {
			const retVal: any = await fs.readJSON(`${__dirname}/../../data/${id}.json`);
			if (Array.isArray(retVal)) {
				if (retVal.length > 0 && retVal[0] instanceof Object) {
					this.datasets.set(id, retVal);
					return retVal;
				}
			}
			throw new NotFoundError(`Invalid file format`);
		} catch (_error) {
			throw new NotFoundError(`file not found`);
		}
		return [];
	}

	public async addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]> {
		try {
			if (!this.isValidId(id)) {
				throw new InsightError("Invalid id");
			}
			try {
				await fs.mkdir(`${__dirname}/../../data`);
			} catch (error: any) {
				if (!(error.code === "EEXIST")) {
					throw error;
				}
			}

			const alreadyExists: String[] = await fs.readdir(`${__dirname}/../../data`);
			if (this.datasets.has(id) || alreadyExists.includes(`${id}.json`)) {
				throw new InsightError("Dataset already exists");
			}

			let processedContent: Section[] | Room[];
			if (kind === InsightDatasetKind.Sections) {
				processedContent = await DatasetProcessor.processSectionsContent(content);
			} else if (kind === InsightDatasetKind.Rooms) {
				processedContent = await DatasetProcessor.processRoomsContent(content);
			} else {
				throw new InsightError("Unsupported dataset kind");
			}

			if (processedContent.length === 0) {
				throw new InsightError("No valid data found in dataset");
			}

			this.datasets.set(id, processedContent);
			await fs.writeJSON(`${__dirname}/../../data/${id}.json`, processedContent);
			return Array.from(this.datasets.keys());
		} catch (error) {
			throw new InsightError("Error adding dataset" + error);
		}
	}

	private isValidId(id: string): boolean {
		return /^[^_]+$/.test(id) && id.trim().length > 0;
	}

	public async removeDataset(id: string): Promise<string> {
		if (!this.isValidId(id)) {
			throw new InsightError("Invalid id");
		}

		const existsInData = await fs.pathExists(`${__dirname}/../../data/${id}.json`);
		const existsInMemory = this.datasets.has(id);
		if (!existsInData && !existsInMemory) {
			throw new NotFoundError("Dataset not found");
		}

		if (existsInMemory) {
			this.datasets.delete(id);
		}

		if (existsInData) {
			await fs.remove(`${__dirname}/../../data/${id}.json`);
		}

		return id;
	}

	public getOptions(options: unknown, mfields: string[], sfields: string[]): OptionResult {
		let onlyID = "";
		let orderField = "";
		let colVals: string[] = [];
		if (typeof options === "object" && options !== null) {
			if ("COLUMNS" in options) {
				const cols = options.COLUMNS;
				try {
					colVals = validateCols(cols, mfields, sfields, []);
					onlyID = colVals[0].split("_")[0];
				} catch {
					throw new InsightError(`No cols`);
				}
			}
			if ("ORDER" in options) {
				const order: unknown = options.ORDER;
				if (typeof order === "string") {
					if (colVals.includes(order)) {
						orderField = order.split("_")[1];
					} else {
						throw new InsightError(`Invalid order field`);
					}
				} else {
					throw new InsightError(`Order field not a string`);
				}
			}
		}
		return { onlyID: onlyID, colVals: colVals, orderField: orderField };
	}

	public async performQuery(query: unknown): Promise<InsightResult[]> {
		try {
			let where: unknown;
			let options: unknown;

			if (!validateQuery(query)) {
				throw new InsightError(`Invalid format for query`);
			}

			if (typeof query === "object" && query && "WHERE" in query && "OPTIONS" in query) {
				where = query.WHERE;
				options = query.OPTIONS;
			}

			const optionsData: OptionResult = this.getOptions(options, InsightFacade.MFIELDS, InsightFacade.SFIELDS);
			const sections = await this.getDataset(optionsData.onlyID);
			let results: Section[] = [];

			if (typeof sections !== "undefined") {
				results = this.runFilter(where, optionsData.onlyID, sections);
			}

			if (results.length > InsightFacade.MAX_RESULTS) {
				throw new ResultTooLargeError("Result too large");
			}

			const orderField: string = optionsData.orderField;
			if (orderField !== "") {
				results.sort((a, b) => (a[orderField as keyof Section] < b[orderField as keyof Section] ? -1 : 1));
			}

			return this.mapResults(results, optionsData.colVals);
		} catch (error) {
			// NotFoundError from getDataset must be caught and converted to InsightError
			// since performQuery only returns InsightError or ResultTooLargeError
			if (error instanceof NotFoundError) {
				throw new InsightError("Error performing query: " + error);
			}
			throw error;
		}
	}

	private mapResults(results: Section[], colVals: string[]): InsightResult[] {
		return results.map((section) => {
			const result: InsightResult = {};
			for (const colKey of colVals) {
				const field = colKey.split("_")[1] as keyof Section;
				result[colKey] = section[field];
			}
			return result;
		});
	}

	public runFilter(obj: unknown, onlyID: string, current: any): any {
		if (typeof obj === "object" && obj !== null) {
			if ("NOT" in obj) {
				const inverse: Section[] = this.runFilter(obj.NOT, onlyID, current);
				return current.filter((section: Section) => !inverse.includes(section));
			}
			if ("AND" in obj) {
				if (Array.isArray(obj.AND)) {
					const and: unknown[] = obj.AND;
					for (const query of and) {
						current = this.runFilter(query, onlyID, current);
					}
					return current;
				}
			}
			if ("OR" in obj) {
				if (Array.isArray(obj.OR)) {
					const or: unknown[] = obj.OR;
					let builtArray: Section[] = [];
					for (const query of or) {
						const filteredSections = this.runFilter(query, onlyID, current).filter(
							(section: Section) => !builtArray.includes(section)
						);

						builtArray = builtArray.concat(filteredSections);
					}
					return builtArray;
				}
			}
			if (isEmpty(obj)) {
				return current;
			}
			return this.runSMFilter(obj, current);
		}
		return [];
	}

	public mComparisons(comp: string, val: number, mval: number): boolean {
		switch (comp) {
			case "LT":
				return val < mval;
			case "EQ":
				return val === mval;
			case "GT":
				return val > mval;
		}
		throw new InsightError(`Invalid mcomp comparator`);
	}

	public runMFilter(obj: unknown, current: Section[]): Section[] {
		if (typeof obj === "object" && obj !== null) {
			let mcomp: unknown = null;
			let comp = "";
			if ("LT" in obj) {
				mcomp = obj.LT;
				comp = "LT";
			}
			if ("GT" in obj) {
				mcomp = obj.GT;
				comp = "GT";
			}
			if ("EQ" in obj) {
				mcomp = obj.EQ;
				comp = "EQ";
			}
			if (typeof mcomp === "object" && mcomp !== null) {
				const mkey = Object.keys(mcomp)[0].split("_")[1];
				const mval = Object.values(mcomp)[0];
				try {
					return current.filter((section) => this.mComparisons(comp, section[mkey as keyof Section], mval));
				} catch {
					throw new InsightError("mkey not found");
				}
			}
		}
		return [];
	}

	public runSMFilter(obj: unknown, current: Section[]): Section[] {
		if (typeof obj === "object" && obj !== null) {
			if ("IS" in obj) {
				const scomp: unknown = obj.IS;
				if (typeof scomp === "object" && scomp !== null) {
					const skey = Object.keys(scomp)[0].split("_")[1];
					let sval = Object.values(scomp)[0];
					try {
						sval = "^" + sval.replace(/\*/gi, ".*") + "$";
						const regex = new RegExp(sval);
						return current.filter((section) => {
							return regex.test(section[skey as keyof Section]);
						});
					} catch {
						throw new InsightError("skey not found");
					}
				}
			}
			return this.runMFilter(obj, current);
		}
		return [];
	}

	public async listDatasets(): Promise<InsightDataset[]> {
		const datasets: InsightDataset[] = [];
		const ids: string[] = [];
		const results = [];
		for (const filename of await fs.readdir(`${__dirname}/../../data`)) {
			const id: string = filename.split(".")[0];
			ids.push(id);
			results.push(this.getDataset(id));
		}
		const resolved = await Promise.all(results);
		for (let i = 0; i < ids.length; i++) {
			const data = resolved[i];
			let kind: InsightDatasetKind;

			if (data.length > 0 && data[0] instanceof Room) {
				kind = InsightDatasetKind.Rooms;
			} else {
				kind = InsightDatasetKind.Sections;
			}

			const dataset: InsightDataset = {
				id: ids[i],
				kind: kind,
				numRows: resolved[i].length,
			};
			datasets.push(dataset);
		}
		return datasets;
	}
}

import { InsightError } from "./IInsightFacade";
import { isSMComp } from "./SMCompValidationHelpers";
// Recursive check if it is a filter
export function isFilter(obj: unknown, idVal: string, mfields: string[], sfields: string[]): Boolean {
	if (typeof obj === "object" && obj !== null) {
		if (Object.keys(obj).length !== 1) {
			return false;
		}
		// Validate NOT
		if ("NOT" in obj) {
			return isFilter(obj.NOT, idVal, mfields, sfields);
		}

		// Validate AND and OR the same way
		if ("AND" in obj || "OR" in obj) {
			// Check if the value is an array
			if (Object.values(obj).length === 1) {
				const val = Object.values(obj)[0];
				if (Array.isArray(val)) {
					if (val.length < 1) {
						return false;
					}
					// Check that no object is not a filter
					return !val.some((item) => {
						return !isFilter(item, idVal, mfields, sfields);
					});
				}
			}
			return false;
		} else {
			return isSMComp(obj, mfields, sfields, idVal);
		}
	}
	return true;
}

export function validateCols(cols: unknown, mfields: string[], sfields: string[], applykeys: string[]): string[] {
	let onlyID: string;
	const keySections = 2;
	if (Array.isArray(cols)) {
		if (cols.length === 0 || typeof cols[0] !== "string") {
			throw new InsightError("Incorrect format");
		}
		onlyID = applykeys.length > 0 ? applykeys[0].split("_")[0] : cols[0].split("_")[0];
		for (const val of cols) {
			if (typeof val === "string") {
				if (applykeys.length > 0) {
					if (!applykeys.includes(val)) {
						throw new InsightError("Not a valid key");
					}
				} else {
					if (val.split("_", keySections)[0] !== onlyID) {
						throw new InsightError("More than one id");
					}
					if (val.split("_", keySections).length < keySections) {
						throw new InsightError("No cols after underscore");
					}
					if (!mfields.includes(val.split("_", keySections)[1]) && !sfields.includes(val.split("_", keySections)[1])) {
						throw new InsightError("Not a key");
					}
				}
			} else {
				throw new InsightError("Not a string");
			}
		}
		return cols;
	} else {
		throw new InsightError("Incorrect col format");
	}
}

// Check for {}
export function isEmpty(obj: unknown): Boolean {
	if (typeof obj === "object" && obj !== null) {
		return Object.keys(obj).length === 0;
	}
	return false;
}

export function validateOrder(order: unknown, colVals: string[]): Boolean {
	if (typeof order === "string") {
		if (colVals.includes(order)) {
			return true;
		}
	} else if (typeof order === "object" && order !== null) {
		if ("dir" in order && "keys" in order) {
			if (order.dir === "UP" || order.dir === "DOWN") {
				if (Array.isArray(order.keys)) {
					if (order.keys.every((element) => colVals.includes(element))) {
						return true;
					}
				}
			}
		}
	}
	return false;
}

export function validateWhere(where: unknown, idVal: string, mfields: string[], sfields: string[]): Boolean {
	if (!isEmpty(where) && !isFilter(where, idVal, mfields, sfields)) {
		return false;
	}
	return true;
}

function initialValidateQuery(query: unknown): { where: unknown; options: unknown; applyKeys: string[] } | false {
	const mfields: string[] = MFIELDS;
	const sfields: string[] = SFIELDS;
	let where: unknown = {};
	let options: unknown = {};
	let applykeys: string[] = [];
	let maxQueryKeys = 2;

	if (typeof query === "object" && query && "WHERE" in query && "OPTIONS" in query) {
		where = query.WHERE;
		options = query.OPTIONS;

		if ("TRANSFORMATIONS" in query) {
			maxQueryKeys += 1;
			applykeys = validateTransformations(query.TRANSFORMATIONS, mfields, sfields);
		}
		if (Object.keys(query).length > maxQueryKeys) {
			return false;
		}
		return { where, options, applyKeys: applykeys };
	}

	return false;
}

export function validateQuery(query: unknown): Boolean {
	try {
		const validationResult = initialValidateQuery(query);
		if (!validationResult) {
			return false;
		}
		const { where, options, applyKeys } = validationResult;
		let colVals: string[] = [];
		let idVal = "";

		if (typeof options === "object" && options !== null) {
			if ("COLUMNS" in options) {
				const cols = options.COLUMNS;
				colVals = validateCols(cols, MFIELDS, SFIELDS, applyKeys);
			} else {
				return false;
			}
			if ("ORDER" in options && !validateOrder(options.ORDER, colVals)) {
				return false;
			}
		} else {
			return false;
		}

		idVal = (applyKeys.length > 0 ? applyKeys[0] : colVals[0]).split("_")[0];
		return validateWhere(where, idVal, MFIELDS, SFIELDS);
	} catch {
		return false;
	}
}

export function validateTransformations(transformations: unknown, mfields: string[], sfields: string[]): string[] {
	let retArray: string[] = [];
	if (typeof transformations === "object" && transformations !== null) {
		if ("GROUP" in transformations && "APPLY" in transformations) {
			if (Array.isArray(transformations.GROUP) && Array.isArray(transformations.APPLY)) {
				const group = transformations.GROUP;
				const apply = transformations.APPLY;
				retArray = validateGroup(group, mfields, sfields);
				return retArray.concat(validateApply(apply, mfields, sfields, retArray[0].split("_")[0]));
			}
		}
	}
	throw new InsightError("Invalid transformation");
}

export function validateGroup(group: unknown[], mfields: string[], sfields: string[]): string[] {
	const keySections = 2;
	if (group.length === 0) {
		throw new InsightError("Invalid grouping");
	}
	if (typeof group[0] !== "string") {
		throw new InsightError("Invalid grouping");
	}
	const onlyID: string = group[0].split("_")[0];
	for (const val of group) {
		if (typeof val === "string") {
			if (val.split("_", keySections)[0] !== onlyID) {
				throw new InsightError("More than one id");
			}
			if (val.split("_", keySections).length < keySections) {
				throw new InsightError("No cols after underscore");
			}
			if (!mfields.includes(val.split("_", keySections)[1]) && !sfields.includes(val.split("_", keySections)[1])) {
				throw new InsightError("Not a key");
			}
		} else {
			throw new InsightError("Not a string");
		}
	}
	return group as string[];
}

export function validateApply(apply: unknown[], mfields: string[], sfields: string[], onlyID: string): string[] {
	const retVal: string[] = [];
	for (const item of apply) {
		retVal.push(validateApplyRule(item, mfields, sfields, onlyID));
	}
	if (new Set(retVal).size !== retVal.length) {
		throw new InsightError("Duplicate keys");
	}
	return retVal;
}

export function validateApplyRule(item: unknown, mfields: string[], sfields: string[], onlyID: string): string {
	if (typeof item === "object" && item !== null) {
		if (Object.keys(item).length === 1) {
			const key = Object.keys(item)[0];
			if (!key.includes("_")) {
				if (Object.values(item).length === 1) {
					if (validateApplyToken(Object.values(item)[0], mfields, sfields, onlyID)) {
						return key;
					}
				}
			}
		}
	}
	throw new InsightError("Apply Rule invalid");
}

export function validateApplyToken(item: unknown, mfields: string[], sfields: string[], onlyID: string): boolean {
	const keySections = 2;
	const applyTokens: string[] = ["MAX", "MIN", "AVG", "COUNT", "SUM"];
	if (typeof item === "object" && item !== null) {
		if (Object.keys(item).length === 1) {
			if (applyTokens.includes(Object.keys(item)[0])) {
				if (Object.values(item).length === 1) {
					const key: unknown = Object.values(item)[0];
					if (typeof key === "string") {
						if (key.split("_", keySections)[0] === onlyID) {
							if (key.split("_", keySections).length === keySections) {
								if (
									mfields.includes(key.split("_", keySections)[1]) ||
									sfields.includes(key.split("_", keySections)[1])
								) {
									return true;
								}
							}
						}
					}
				}
			}
		}
	}
	return false;
}

export interface OptionResult {
	onlyID: string;
	colVals: string[];
	orderField: string;
}

export const requiredFields: Record<string, string> = {
	id: "number",
	Course: "string",
	Title: "string",
	Professor: "string",
	Subject: "string",
	Year: "string",
	Avg: "number",
	Pass: "number",
	Fail: "number",
	Audit: "number",
};

export const MFIELDS = ["avg", "pass", "fail", "audit", "year", "lat", "lon", "seats"];
export const SFIELDS = [
	"dept",
	"id",
	"instructor",
	"title",
	"uuid",
	"fullname",
	"shortname",
	"number",
	"name",
	"address",
	"type",
	"furniture",
	"href",
];

export function isSCOMP(filter: unknown, sfields: string[], idVal: string): Boolean {
	const keySections = 2;
	const match = new RegExp("^[*]?[^*]*[*]?$");
	if (typeof filter === "object" && filter !== null) {
		if (Object.keys(filter).length === 1) {
			const skey: string = Object.keys(filter)[0];
			if (skey.split("_").length === keySections) {
				const splitKey: string[] = skey.split("_");
				if (splitKey[0] !== idVal) {
					return false;
				}
				if (!sfields.includes(splitKey[1])) {
					return false;
				}
			} else {
				return false;
			}
		} else {
			return false;
		}
		if (Object.values(filter).length === 1 && typeof Object.values(filter)[0] === "string") {
			const sval: string = Object.values(filter)[0];
			return match.test(sval);
		}
	}
	return false;
}

export function isMCOMP(filter: unknown, mfields: string[], idVal: string): Boolean {
	const keySections = 2;
	if (typeof filter === "object" && filter !== null) {
		if (Object.keys(filter).length === 1) {
			const mkey: string = Object.keys(filter)[0];
			if (mkey.split("_").length === keySections) {
				const splitKey: string[] = mkey.split("_");
				if (splitKey[0] !== idVal) {
					return false;
				}
				if (!mfields.includes(splitKey[1])) {
					return false;
				}
			} else {
				return false;
			}
		} else {
			return false;
		}
		if (Object.values(filter).length === 1 && typeof Object.values(filter)[0] === "number") {
			return true;
		}
	}
	return false;
}

export function isSMComp(filter: unknown, mfields: string[], sfields: string[], idVal: string): Boolean {
	if (typeof filter === "object" && filter !== null) {
		if ("IS" in filter) {
			const scomp: unknown = filter.IS;
			return isSCOMP(scomp, sfields, idVal);
		}
		if ("LT" in filter) {
			const mcomp: unknown = filter.LT;
			return isMCOMP(mcomp, mfields, idVal);
		}
		if ("GT" in filter) {
			const mcomp: unknown = filter.GT;
			return isMCOMP(mcomp, mfields, idVal);
		}
		if ("EQ" in filter) {
			const mcomp: unknown = filter.EQ;
			return isMCOMP(mcomp, mfields, idVal);
		}
		return false;
	}
	return false;
}

// DatasetProcessor.ts
import JSZip from "jszip";
import { InsightError } from "./IInsightFacade";
import Section from "./Section";
import Room from "./Room";
import * as parse5 from "parse5";
import { ChildNode, Document } from "parse5/dist/tree-adapters/default";
import {
	findTableWithClass,
	getBuildingRows,
	getBuildingTable,
	getHrefFromLink,
	getLinkText,
	getRoomRows,
	getTableCells,
	getTextContent,
} from "./HtmlParseHelpers";
import GeolocationService from "./GeolocationService";

interface BuildingData {
	fullName: string;
	shortName: string;
	address: string;
	roomsLink: string;
	lat?: number;
	lon?: number;
}

interface RoomData {
	number: string;
	seats: number;
	furniture: string;
	type: string;
	href: string;
}

export default class DatasetProcessor {
	public static async processSectionsContent(content: string): Promise<Section[]> {
		try {
			const buf = Buffer.from(content, "base64");
			const zip = new JSZip();
			const zipContent = await zip.loadAsync(buf);
			const sections = await this.createValidSectionsFromZip(zipContent);
			if (sections.length === 0) {
				throw new InsightError("No valid sections found");
			}
			return sections;
		} catch (e) {
			throw new InsightError("Error processing content: " + e);
		}
	}

	private static async createValidSectionsFromZip(zipContent: JSZip): Promise<Section[]> {
		const sections: Section[] = [];
		const promises: Promise<void>[] = [];

		zipContent.forEach((relativePath: string, file: JSZip.JSZipObject) => {
			if (!relativePath.startsWith("courses/") || file.dir) {
				return;
			}
			const promise = file.async("string").then((fileContent: string) => {
				if (fileContent.trim().length === 0) {
					return;
				}
				try {
					const json = JSON.parse(fileContent);
					if (json && Array.isArray(json.result)) {
						for (const course of json.result) {
							const section = Section.createSection(course);
							if (section) {
								sections.push(section);
							}
						}
					}
				} catch {
					// Ignore invalid JSON files
				}
			});
			promises.push(promise);
		});

		await Promise.all(promises);
		return sections;
	}

	public static async processRoomsContent(content: string): Promise<Room[]> {
		try {
			const buf = Buffer.from(content, "base64");
			const zip = new JSZip();
			const zipContent = await zip.loadAsync(buf);
			const rooms = await this.createValidRoomsFromZip(zipContent);
			if (rooms.length === 0) {
				throw new InsightError("No valid rooms found");
			}
			return rooms;
		} catch (e) {
			throw new InsightError("Error processing content: " + e);
		}
	}

	private static async createValidRoomsFromZip(zipContent: JSZip): Promise<Room[]> {
		let indexFile: JSZip.JSZipObject | null = null;
		zipContent.forEach((relativePath: string, file: JSZip.JSZipObject) => {
			if (relativePath.endsWith("index.htm")) {
				indexFile = file;
			}
		});

		if (!indexFile) {
			throw new InsightError("index file does not exist");
		}

		const indexStringContent = await (indexFile as JSZip.JSZipObject).async("string");

		const indexDoc: Document = parse5.parse(indexStringContent);

		const buildingTable = getBuildingTable(indexDoc.childNodes);
		if (!buildingTable) {
			throw new InsightError("building table not found");
		}

		const buildingRows = getBuildingRows(buildingTable);
		if (buildingRows.length === 0) {
			throw new InsightError("No building rows found in index.htm");
		}

		const buildingPromises = buildingRows.map(async (row) => {
			const buildingData = this.getBuildingDataFromRow(row);
			if (!buildingData) {
				return [];
			}
			return this.getRoomsForBuilding(buildingData, zipContent);
		});

		const roomArrays = await Promise.all(buildingPromises);
		return roomArrays.flat();
	}

	private static async getRoomsForBuilding(buildingData: BuildingData, zipContent: JSZip): Promise<Room[]> {
		const buildingFile = this.findBuildingFile(zipContent, buildingData.roomsLink);
		if (!buildingFile) {
			return [];
		}

		const buildingDoc = await this.parseBuildingFile(buildingFile);
		const roomsTable = findTableWithClass(buildingDoc, "views-table");
		if (!roomsTable) {
			return [];
		}

		const roomRows = getRoomRows(roomsTable);
		return this.extractRoomsFromRows(roomRows, buildingData);
	}

	private static findBuildingFile(zipContent: JSZip, link: string): JSZip.JSZipObject | null {
		const path = this.normalizeZipPath(link);
		return this.findFileInZip(zipContent, path);
	}

	private static async parseBuildingFile(buildingFile: JSZip.JSZipObject): Promise<Document> {
		const content = await buildingFile.async("string");
		return parse5.parse(content);
	}

	private static async extractRoomsFromRows(roomRows: ChildNode[], buildingData: BuildingData): Promise<Room[]> {
		// Create an array of promises to process rooms in parallel
		const roomPromises = roomRows.map(async (row) => {
			try {
				const cells = getTableCells(row);
				const roomData = this.getRoomDataFromCells(cells);
				if (roomData) {
					const name = `${buildingData.shortName}_${roomData.number}`;

					// Fetch geolocation data for the building address
					const geoResponse = await GeolocationService.getGeolocation(buildingData.address);

					if (geoResponse.error) {
						throw new InsightError(`No location found for ${name}`);
					}

					// Return the created Room instance
					return new Room(
						buildingData.fullName,
						buildingData.shortName,
						roomData.number,
						name,
						buildingData.address,
						geoResponse.lat!,
						geoResponse.lon!,
						roomData.seats,
						roomData.type,
						roomData.furniture,
						roomData.href
					);
				}
			} catch {
				return null; // Skip invalid rows
			}
		});

		// Wait for all room promises to complete and filter out any null results
		const rooms = await Promise.all(roomPromises);
		return rooms.filter((room): room is Room => room !== null);
	}

	private static findFileInZip(zipContent: JSZip, targetPath: string): JSZip.JSZipObject | null {
		let foundFile: JSZip.JSZipObject | null = null;
		const normalizedTarget = this.normalizeZipPath(targetPath);

		zipContent.forEach((relativePath: string, file: JSZip.JSZipObject) => {
			const normalizedPath = this.normalizeZipPath(relativePath);

			if (normalizedPath.endsWith(normalizedTarget)) {
				foundFile = file;
			}
		});

		return foundFile;
	}

	private static normalizeZipPath(path: string): string {
		path = path.replace(/^\.\//, "").replace(/\\/g, "/");
		return path.replace(/^\//, "");
	}

	private static getRoomDataFromCells(cells: ChildNode[]): RoomData | null {
		let number = "",
			seats = 0,
			furniture = "",
			type = "",
			href = "";

		for (const cell of cells) {
			if ("attrs" in cell) {
				const classAttr = cell.attrs?.find((attr) => attr.name === "class")?.value || "";

				if (classAttr.includes("views-field-field-room-number")) {
					number = getLinkText(cell);
					href = getHrefFromLink(cell);
				} else if (classAttr.includes("views-field-field-room-capacity")) {
					const seatsText = getTextContent(cell);
					seats = parseInt(seatsText, 10);
				} else if (classAttr.includes("views-field-field-room-furniture")) {
					furniture = getTextContent(cell);
				} else if (classAttr.includes("views-field-field-room-type")) {
					type = getTextContent(cell);
				}
			}
		}

		if (number && seats && furniture && type) {
			return {
				number,
				seats,
				furniture,
				type,
				href,
			};
		}
		return null;
	}

	private static getBuildingDataFromRow(buildingRow: ChildNode): BuildingData | null {
		let fullName = "",
			shortName = "",
			address = "",
			roomsLink = "";

		if ("childNodes" in buildingRow) {
			for (const child of buildingRow.childNodes) {
				if (child.nodeName === "td" && child.attrs) {
					const classAttr = child.attrs.find((attr) => attr.name === "class")?.value || "";

					// Extract the building code
					if (classAttr.includes("views-field-field-building-code")) {
						shortName = getTextContent(child);
					}

					// Extract the building name
					if (classAttr.includes("views-field-title")) {
						fullName = getLinkText(child); // Updated to handle <a> tag
						roomsLink = getHrefFromLink(child);
					}

					// Extract the building address
					if (classAttr.includes("views-field-field-building-address")) {
						address = getTextContent(child);
					}
				}
			}
		}

		if (fullName && shortName && address) {
			return { fullName, shortName, address, roomsLink };
		}
		return null;
	}
}

import { requiredFields } from "./ValidationHelpers";

export default class Section {
	private readonly uuid: string;
	private readonly id: string;
	private readonly title: string;
	private readonly instructor: string;
	private readonly dept: string;
	private readonly year: number;
	private readonly avg: number;
	private readonly pass: number;
	private readonly fail: number;
	private readonly audit: number;

	public constructor(
		uuid: string,
		id: string,
		title: string,
		instructor: string,
		dept: string,
		year: number,
		avg: number,
		pass: number,
		fail: number,
		audit: number
	) {
		this.uuid = uuid;
		this.id = id;
		this.title = title;
		this.instructor = instructor;
		this.dept = dept;
		this.year = year;
		this.avg = avg;
		this.pass = pass;
		this.fail = fail;
		this.audit = audit;
	}

	public static createSection(course: any): Section | null {
		// Check for the presence and type of each required field
		for (const [field, type] of Object.entries(requiredFields)) {
			if (!(field in course) || typeof course[field] !== type) {
				return null;
			}
		}
		let tempYear = 1900;
		try {
			if (course.Section !== "overall") {
				if (isNaN(course.Year)) {
					return null;
				}
				tempYear = Number(course.Year);
			}
		} catch {
			return null;
		}
		return new Section(
			course.id.toString(),
			course.Course,
			course.Title,
			course.Professor,
			course.Subject,
			tempYear,
			course.Avg,
			course.Pass,
			course.Fail,
			course.Audit
		);
	}
}

